<?php\n// Assume $filePath is where your kickstart config file is stored on the server\n$filePath = 'path/to/your/config.ks';\n\nif (file_exists($filePath)) {\n    // Set headers to force download\n    header('Content-Description: File Transfer');\n    header('Content-Type: application/octet-stream');\n    header('Content-Disposition: attachment; filename="kickstart-config.ks"'); // Specify desired file name\n    header('Expires: 0');\n    header('Cache-Control: must-revalidate');\n    header('Pragma: public');\n    header('Content-Length: ' . filesize($filePath));\n    readfile($filePath);\n    exit;\n} else {\n    echo "File not found.";\n}\n?>