<?php\n$input = "Ryu Aiter D78:21:87:13 177 /177 1 / 6\nRyu Chronos D78:21:26:21 182 /182 0 / 6\nRyu Hermes D78:21:26:22 201 /201 0 / 6\nRyu Hefaistos D78:31:75:10 136 /136 1 / 2\nRyu Krotos D78:84:96:11 170 /170 1 / 6\nRyu Heros D78:65:51:31 175 /175 2 / 5\nRyu Arachnos D78:13:84:11 185 /185 0 / 5";\n\n$lines = explode("\n", $input);\n\nforeach ($lines as $line) {\n    // Remove any extra spaces\n    $line = trim($line);\n    \n    // Assuming the format is consistent, we can split by space and use substr to get location\n    $parts = preg_split('/\s+/', $line);\n    \n    $base = $parts[0] . ' ' . $parts[1]; // Base (up to 16 chars)\n    $location = substr($line, strpos($line, 'D'), 12); // Location starting with D\n    \n    // Extract econ and max econ\n    preg_match('/(\d+) \/(\d+)/', $line, $econMatch);\n    $econ = $econMatch[1];\n    $maxEcon = $econMatch[2];\n    \n    // Extract used and total\n    preg_match('/ (\d+) \/ (\d+)$/', $line, $usageMatch);\n    $used = $usageMatch[1];\n    $total = $usageMatch[2];\n    \n    echo "Base: $base\n";\n    echo "Location: $location\n";\n    echo "Econ/Max Econ: $econ / $maxEcon\n";\n    echo "Used/Total: $used / $total\n\n";\n}\n?>