$yourForm = new Zend_Form();\n\n$dateElement = $yourForm->addElement('text', 'date_field', array(\n    'label' => 'Enter Date (dd/mm/yyyy):',\n    'required' => true,\n    'filters' => array(\n        // A custom filter to convert date format on form submission\n        function ($value) {\n            if (empty($value)) return null;\n            $date = Zend_Date::factory($value, 'dd/MM/yyyy');\n            return $date->toString('yyyy-MM-dd');\n        }\n    ),\n    'validators' => array(\n        new Zend_Validate_Date(array('format' => 'dd/MM/yyyy'))\n    )\n));