<?php\n// Enable error reporting\nerror_reporting(E_ALL);\nini_set('display_errors', 1);\n\ntry {\n    // Set up the database connection\n    $pdo = new PDO("sqlsrv:Server=your_server;Database=your_db", "username", "password");\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare your SQL statement\n    $stmt = $pdo->prepare("INSERT INTO your_table (column1, column2) VALUES (:value1, :value2)");\n\n    // Bind parameters to the prepared statement\n    $stmt->bindParam(':value1', $value1);\n    $stmt->bindParam(':value2', $value2);\n\n    // Example values\n    $value1 = 'exampleValue1';\n    $value2 = 'exampleValue2';\n\n    // Execute the statement\n    if ($stmt->execute()) {\n        echo "Insert successful!";\n    } else {\n        echo "Insert failed.";\n    }\n} catch (PDOException $e) {\n    // Log the error for debugging purposes\n    echo "Error: " . $e->getMessage();\n    file_put_contents('error_log.txt', "Database Error [".date("Y-m-d H:i:s")."]: ". $e->getMessage()."\n", FILE_APPEND);\n}\n?>