// Using PDO for example\nfunction DoSomething($pdo) {\n    // Example query execution\n    $stmt = $pdo->query("SELECT * FROM users");\n    $users = $stmt->fetchAll();\n\n    echo "Number of users: " . $stmt->rowCount();\n}\n\nfunction main() {\n    // Establishing a new PDO connection\n    $pdo = new PDO('mysql:host=localhost;dbname=testdb', 'username', 'password');\n\n    // Passing the pdo object to function by value\n    DoSomething($pdo);\n}