<?php\n// Example user input\n$subject_prefix = "O'Reilly's Test\nNext line";\n$msg_footer = "Here's \"another\" test";\n\n// Function to escape Python strings safely\nfunction escape_for_python($string) {\n    // Replace single quotes with escaped single quotes\n    $escapedString = str_replace("'", "\\'", $string);\n    // Escape backslashes\n    $escapedString = str_replace("\\", "\\\\", $escapedString);\n    return $escapedString;\n}\n\n// Output the Python script\necho "subject_prefix = '" . escape_for_python($subject_prefix) . "'\n";\necho "msg_footer = '''" . escape_for_python($msg_footer) . "'''\n";\n?>