<?php\n// Ensure the input is safe to store\n$from = htmlspecialchars($_POST['From'], ENT_QUOTES, 'UTF-8');\n$messageBody = htmlspecialchars($_POST['Body'], ENT_QUOTES, 'UTF-8');\n\n// Connect to your database (replace with actual connection details)\n$conn = new mysqli('localhost', 'username', 'password', 'database_name');\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Insert message into the database\n$stmt = $conn->prepare("INSERT INTO sms_messages (from_number, message_body) VALUES (?, ?)");\n$stmt->bind_param("ss", $from, $messageBody);\n\nif ($stmt->execute()) {\n    echo "Message stored successfully.";\n} else {\n    error_log($conn->error);\n    echo "Error: could not store message.";\n}\n\n// Close connections\n$stmt->close();\n$conn->close();\n\n// Respond appropriately to Twilio (a simple 204 response is sufficient)\nhttp_response_code(204);\n?>