// Function to send stats to mother server\nfunction sendStatsToMotherServer($motherServerUrl, $statsData) {\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $motherServerUrl);\n    curl_setopt($ch, CURLOPT_POST, 1);\n    curl_setopt($ch, CURLOPT_POSTFIELDS, json_encode($statsData));\n    curl_setopt($ch, CURLOPT_HTTPHEADER, ['Content-Type: application/json']);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n\n    $response = curl_exec($ch);\n    curl_close($ch);\n\n    return $response;\n}\n\n// Example usage\n$statsToSend = ['server_id' => 1, 'cpu_usage' => 75, 'memory_usage' => 80];\n$response = sendStatsToMotherServer('http://mother.com/collect_stats.php', $statsToSend);