<?php\n\n// Ensure the id3v2 extension is enabled and working.\nif (!extension_loaded('id3')) {\n    die("The id3v2 extension is required but not loaded.");\n}\n\n$file = 'path/to/your/file.mp3';\n$imageFile = 'path/to/your/image.jpg';\n\n// Open the MP3 file\n$id3info =& new id3($file);\n\n// Set album art. The MIME type should match your image type.\n$result = $id3info->addFrame('APIC',\n    array(\n        "mime" => 'image/jpeg', // MIME type of the image (use 'image/png' if it's a PNG)\n        "desc" => 'Album Cover',\n        "pic"  => file_get_contents($imageFile) // Content of the image file\n    )\n);\n\nif ($result) {\n    echo "Album art added successfully.";\n} else {\n    echo "Error adding album art: " . $id3info->errstr();\n}\n?>