// Include the jpGraph library file(s)\nrequire_once ('jpgraph/jpgraph.php');\nrequire_once ('jpgraph/jpgraph_pie.php');\nrequire_once ('jpgraph/jpgraph_pie3d.php');\n\n// Some data\n$data = array(40, 60, 21, 33);\n\n// Create the Pie Graph.\n$graph = new PieGraph(350,250);\n$graph->SetShadow();\n\n// Set A title for the plot\n$graph->title->Set("Sample Pie Chart");\n$graph->title->SetFont(FF_FONT1,FS_BOLD);\n\n// Create pie plot\n$p1 = new PiePlot($data);\n$p1->SetSize(0.35);\n$p1->SetCenter(0.7, 0.4);\n\n// Use percentage display \n$p1->value->Show();\n$p1->value->SetFormat("%d%%");\n\n// Play with colors a bit\n$p1->SetColor('khaki');\n$p1->SetSliceColors(array('#ffeb99','#c4ff65',"red","#bfbfff"));\n\n// Explode all slices\n$p1->ExplodeAll(10);\n\n// Add plot to graph\n$graph->Add($p1);\n\n// Send back the HTML page which is then rendered by browser\n$graph->Stroke();