// Example in PHP for setting up routes programmatically\n$frontController = Zend_Controller_Front::getInstance();\n$router = $frontController->getRouter();\n\n$adminRoute = new Zend_Controller_Router_Route(\n    'admin',\n    array('module' => 'admin', 'controller' => 'index', 'action' => 'index')\n);\n$loginRoute = new Zend_Controller_Router_Route(\n    'login',\n    array('module' => 'admin', 'controller' => 'login', 'action' => 'index')\n);\n\n$adminLoginChain = $router->chain($adminRoute, $loginRoute);\n$router->addRoute('admin', $adminRoute);\n$router->addRoute('adminLogin', $adminLoginChain);