<?php\n// Start the session or connect to memcache\n$memcache = new Memcache;\n$memcache->connect('localhost', 11211) or die ("Could not connect");\n\n// Data to be stored as an array\n$userData = [\n    'user_id' => 12345,\n    'photo_url' => 'http://example.com/photo.jpg',\n    'name' => 'John Doe'\n];\n\n// Store the array in Memcache with a unique key\n$memcache->set('user_12345_data', $userData, false, 0);\n\n// Optionally, retrieve and print the data to verify storage\n$retrievedData = $memcache->get('user_12345_data');\nprint_r($retrievedData);\n?>