<?php\n// Start the session\nsession_start();\n\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    if (isset($_POST['key'])) {\n        $key = $_POST['key'];\n        // Use $key for your database query or other operations\n        echo "The selected key is: " . htmlspecialchars($key);\n    } else {\n        echo "No key found.";\n    }\n} else {\n    header("Location: index.php"); // Redirect if not a POST request\n    exit();\n}\n?>