<?php\n// Start the session\nsession_start();\n\n// Assume $keys is an array of keys you want to iterate over\n$keys = ['key1', 'key2', 'key3'];\n\nforeach ($keys as $val) {\n    echo '<tr><td>';\n    // Create a form that submits to the next page with hidden field containing the key\n    echo '<form action="nextPage.php" method="post" style="display:inline;">';\n    echo "<input type='hidden' name='key' value='$val'>";\n    echo "<button type='submit'>$val</button>";\n    echo '</form>';\n    echo '</td></tr>';\n}\n?>