function saveFieldEntries($data) {\n    // Fetch the last inserted form ID correctly\n    $lastForm = $this->find('first', array(\n        'fields' => array('Form.id'),\n        'order' => 'Form.id DESC'\n    ));\n    \n    // Assign the id value directly\n    if (!empty($lastForm)) {\n        $this->data['Attribute']['form_id'] = $lastForm['Form']['id'];\n        \n        // Set other attribute fields\n        $this->data['Attribute']['label']= 'Label';\n        $this->data['Attribute']['size']='20';\n        $this->data['Attribute']['type']=$data['Attribute']['type'];\n        $this->data['Attribute']['sequence_no'] = $data['Attribute']['sequence_no'];\n        \n        // Save the attribute record\n        $this->Attribute->save($this->data);\n    } else {\n        // Handle the case where no form was found\n        throw new Exception("No forms found to associate with this attribute.");\n    }\n}