$db_connect = new mysqli(DB_HOST, DB_USER, DB_PASSWORD, DB_NAME);\n\nif ($db_connect->connect_error) {\n    die("Connection failed: " . $db_connect->connect_error);\n}\n\n$test_query = "SET @N=-1; SELECT `id`, (@N:=@N+1) AS `mycount` FROM `mydb`;";\n\nif ($db_connect->multi_query($test_query)) {\n    do {\n        // Check if result is available\n        if ($result = $db_connect->store_result()) {\n            // Fetch and display the results\n            while ($row = $result->fetch_row()) {\n                echo $row[0] . " " . $row[1] . "<br />";\n            }\n            // Free result set\n            $result->free();\n        }\n    } while ($db_connect->more_results() && $db_connect->next_result());\n} else {\n    echo "Multi query failed: (" . $db_connect->errno . ") " . $db_connect->error;\n}\n\n$db_connect->close();