class Foo\n{\n    public function __construct()\n    {\n        $bar = new Bar();\n        $bar->test();\n    }\n}\n\nclass Bar\n{\n    public function test()\n    {\n        $backtrace = debug_backtrace(DEBUG_BACKTRACE_IGNORE_ARGS);\n        if (isset($backtrace[1])) { // The caller is at index 1\n            echo "Called from: " . $backtrace[1]['class'] . "::" . $backtrace[1]['function'];\n        }\n    }\n}\n\n$foo = new Foo();