function reportThread($id, $userId) {\n    // Connect to the database securely (use PDO or MySQLi)\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n    \n    // Prepare a SQL statement with placeholders for parameters\n    $stmt = $pdo->prepare("SELECT is_checked FROM reports WHERE url = :url");\n    $stmt->execute([':url' => "?threadID=$id"]);\n    $r = $stmt->fetch(PDO::FETCH_ASSOC);\n    \n    if ($r && $r['is_checked'] == 0) {\n        echo "<script type=\"text/javascript\">alert(\"A moderator has been notified.\");</script>";\n        \n        // Prepare another statement for the insertion\n        $insertStmt = $pdo->prepare("INSERT INTO reports (url, userID) VALUES (:url, :userId)");\n        $insertStmt->execute([':url' => "?threadID=$id", ':userId' => $userId]);\n    } else {\n        echo "<script type=\"text/javascript\">alert(\"There's already a pending report on this thread.\");</script>";\n    }\n}