function peso() {\n         $resultado = utf8_encode(exec('/usr/bin/mono /path/to/LoadCell.exe', $output, $return_var));\n         \n         if ($return_var !== 0) {\n             json_exit(array('error' => 'Failed to execute C# program'));\n         } else {\n             $resultString = implode("\n", $output);\n             if (preg_match('/^RA:\s*([0-9]{1,8})$/i', $resultString, $m) > 0) {\n                 json_exit(array('peso' => $m[1]));\n             } else {\n                 json_exit(array('error' => $resultado));\n             }\n         }\n     }\n\n     function json_exit($data) {\n         header('Content-Type: application/json');\n         echo json_encode($data);\n         exit;\n     }