class User {\n    protected $user_id;\n    protected $email;\n    protected $username;\n    protected $password;\n    protected $company_id;\n    protected $active; // Bool that holds either a 0 or 1\n\n    public function __construct(int $userId, string $email, string $username, string $password, int $companyId, bool $active) {\n        $this->user_id = $userId;\n        $this->email = $email;\n        $this->username = $username;\n        $this->password = password_hash($password, PASSWORD_DEFAULT); // ensure password is hashed\n        $this->company_id = $companyId;\n        $this->active = $active;\n    }\n\n    public function getUserId(): int {\n        return $this->user_id;\n    }\n\n    public function getEmail(): string {\n        return $this->email;\n    }\n\n    public function getUsername(): string {\n        return $this->username;\n    }\n\n    public function isActive(): bool {\n        return $this->active;\n    }\n\n    // Business rule method\n    public static function isValidUser(string $password, User $user): bool {\n        return password_verify($password, $user->password);\n    }\n}