<?php\nrequire_once('includes/MM_XSLTransform/MM_XSLTransform.class.php');\n\n$asin = htmlspecialchars($_GET["asin"]);\nif (empty($asin)) {\n    die("ASIN is missing");\n}\n\n// Fetch XML data from Amazon Web Services\n$xmlUrl = "http://ecs.amazonaws.com/onca/xml?Service=AWSECommerceService&AWSAccessKeyId=[myid]&Operation=ItemLookup&ResponseGroup=Large&ItemId=" . $asin;\n$xmlContent = file_get_contents($xmlUrl);\n\nif ($xmlContent === FALSE) {\n    die("Error fetching XML from Amazon Web Services");\n}\n\n// Uncomment the following line to see the raw XML output (useful for debugging)\n// echo htmlspecialchars($xmlContent, ENT_QUOTES, 'UTF-8');\n\n$mm_xsl = new MM_XSLTransform();\n$success = $mm_xsl->setXML($xmlContent); // Set the XML content as a string\n\nif (!$success) {\n    die("Error setting XML for transformation");\n}\n\n$mm_xsl->setXSL("aws1.xsl");\n\n$outputHtml = $mm_xsl->Transform();\n\nif ($outputHtml === FALSE) {\n    die("Error transforming XML to HTML");\n}\n\necho $outputHtml;\n?>