function getBaseURL() {\n    // Get protocol (http or https)\n    $protocol = (!empty($_SERVER['HTTPS']) && $_SERVER['HTTPS'] !== 'off' || $_SERVER['SERVER_PORT'] == 443) ? "https://" : "http://";\n    \n    // Get host and port\n    $host = $_SERVER['HTTP_HOST'];\n    \n    // Get base path of the application (removing script name)\n    $scriptName = basename($_SERVER['SCRIPT_NAME']);\n    $scriptPath = dirname($_SERVER['SCRIPT_NAME']);\n\n    // Construct base URL\n    if ($scriptPath != "/") {\n        return $protocol . $host . str_replace("/" . $scriptName, "", $_SERVER['REQUEST_URI']);\n    } else {\n        return $protocol . $host;\n    }\n}\n\n// Usage in your application\n$approot = getBaseURL();\necho "Base URL: " . $approot; // Outputs e.g., http://www.example.com/application/