// Initialize cURL session\n$ch = curl_init();\n\n// Set options for the cURL session\ncurl_setopt($ch, CURLOPT_URL, "http://rss.sciam.com/ScientificAmerican-Global?format=xml");\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_USERAGENT, 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/58.0.3029.110 Safari/537.3');\n\n// Execute the cURL session\n$xml_text = curl_exec($ch);\n\n// Check for errors in the cURL session\nif (curl_errno($ch)) {\n    echo 'Error:' . curl_error($ch);\n}\n\n// Close the cURL session\ncurl_close($ch);\n\n// Parse the XML content\n$xml_parser = xml_parser_create();\n$res = xml_parse($xml_parser, $xml_text);\n\nif (!$res) {\n  $error = xml_error_string(xml_get_error_code($xml_parser)) .\n           " at line " .\n           xml_get_current_line_number($xml_parser);\n  echo $error;\n} else {\n    echo "XML parsed successfully!";\n}