<?php\n\n$output = "m0.ttw.mydomain.tel.    60      IN      TXT     \".tkw\" \"1\" \"20090624-183342\" \"Some text here1\"\nm0.ttw.mydomain.tel.    60      IN      TXT     \".tkw\" \"1\" \"20090624-183341\" \"Some text here2\"";\n\npreg_match_all('/"([^"]+)"/', $output, $matches);\n\n$array = array_map(function($item) {\n    return explode('" "', trim($item));\n}, $matches[0]);\n\n// Clean up the array to remove extra quotes\n$array = array_map(function($item) {\n    return preg_replace('/^"|"$/', '', $item);\n}, $array);\n\nprint_r($array);\n?>