<?php\n// Assuming you have a database connection already established\n$mysqli = new mysqli("localhost", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') '\n            . $mysqli->connect_error);\n}\n\n// Get the user's data using a query, assuming you have a parameter like user_id in your URL\n$user_id = intval($_GET['user_id']); // Always sanitize input from GET requests\n\n$query = "SELECT * FROM users WHERE id = ?";\n$stmt = $mysqli->prepare($query);\n$stmt->bind_param("i", $user_id);\n$stmt->execute();\n$result = $stmt->get_result();\n\n// Check if there are any rows\nif ($result->num_rows > 0) {\n    // Set headers for CSV file download\n    header('Content-Type: text/csv; charset=utf-8');\n    header('Content-Disposition: attachment; filename="user_data.csv"');\n\n    // Create a file pointer connected to the output stream\n    $output = fopen('php://output', 'w');\n\n    // Insert the column names (optional)\n    fputcsv($output, array('ID', 'Name', 'Email')); // Adjust column names according to your database schema\n\n    // Fetch each row and write it to CSV file\n    while ($row = $result->fetch_assoc()) {\n        fputcsv($output, $row);\n    }\n\n    fclose($output);\n} else {\n    echo "No data found for the specified user ID.";\n}\n\n$mysqli->close();\n?>