<?php\n// Database connection with PDO\n$dsn = 'mysql:host=localhost;dbname=your_database';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Query the database\n    $stmt = $pdo->query("SELECT id, name, image_blob FROM your_table");\n    $result = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n    // Start XML document\n    echo '<?xml version="1.0" encoding="UTF-8"?>';\n    echo '<items>';\n\n    // Iterate over results and add to XML\n    foreach ($result as $row) {\n        echo '<item>';\n        echo '<id>' . htmlspecialchars($row['id'], ENT_XML1, 'UTF-8') . '</id>';\n        echo '<name>' . htmlspecialchars($row['name'], ENT_XML1, 'UTF-8') . '</name>';\n        // Encode BLOB as base64\n        if ($row['image_blob'] !== null) {\n            $encodedData = base64_encode($row['image_blob']);\n            echo '<image><![CDATA[' . $encodedData . ']]></image>';\n        }\n        echo '</item>';\n    }\n\n    echo '</items>';\n\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}\n?>