<?php\n// Database configuration\n$host = 'your_host'; // Typically 'localhost'\n$username = 'your_username';\n$password = 'your_password';\n$database = 'your_database';\n\n// Backup location on server\n$backupDir = '/path/to/backup/directory/';\n$backupFile = $backupDir . date('Ymd_His') . '_db_backup.sql';\n\n// MySQL dump command\n$command = sprintf(\n    'mysqldump --opt --host=%s --user=%s --password=%s %s > %s',\n    escapeshellarg($host),\n    escapeshellarg($username),\n    escapeshellarg($password),\n    escapeshellarg($database),\n    escapeshellarg($backupFile)\n);\n\n// Execute the command\nsystem($command, $output);\nif ($output != 0) {\n    die('Backup failed!');\n}\n\necho 'Backup completed successfully: ' . basename($backupFile);\n?>