<?php\nif ($_POST) {\n    // Handle form submission logic here\n\n    // Retrieve the referrer URL from the form data\n    $referrer = filter_input(INPUT_POST, 'referrer', FILTER_SANITIZE_URL);\n\n    if (filter_var($referrer, FILTER_VALIDATE_URL)) {\n        // Redirect back to the original referrer URL\n        header("Location: " . $referrer);\n        exit;\n    } else {\n        echo "Invalid referer URL.";\n        // Additional error handling can go here\n    }\n}\n?>