function calculateSMA($prices, $period) {\n    $sma = [];\n    for ($i = $period - 1; $i < count($prices); $i++) {\n        $sum = array_sum(array_slice($prices, $i - $period + 1, $period));\n        $sma[] = $sum / $period;\n    }\n    return $sma;\n}\n\n// Example usage:\n$stockPrices = [20, 21, 23, 24, 26, 28, 30];\n$period = 3;\n$smaValues = calculateSMA($stockPrices, $period);\nprint_r($smaValues);