<?php\n// Define an array with your site identifiers and corresponding stylesheets\n$sites = [\n    'siteA' => 'styleA.css',\n    'siteB' => 'styleB.css'\n];\n\n// Determine the current site identifier (this is just an example, you might get this from a request parameter or session)\n$currentSite = 'siteA'; // Example value; replace with your actual method to determine the site\n\n// Default stylesheet if the site doesn't match any in the array\n$defaultStyle = 'FinalStyle.css';\n\n// Select the stylesheet based on the current site\n$stylesheet = $sites[$currentSite] ?? $defaultStyle;\n?>\n\n<!DOCTYPE html>\n<html lang="en">\n<head>\n    <meta charset="UTF-8">\n    <meta name="viewport" content="width=device-width, initial-scale=1.0">\n    <title>Dynamic Stylesheet</title>\n    <!-- Include the selected stylesheet -->\n    <link rel="stylesheet" href="<?php echo htmlspecialchars($stylesheet); ?>">\n</head>\n<body>\n    <!-- Your website content goes here -->\n</body>\n</html>