<?php\n// Define the list of words\n$WORDS = [\n    "apple", "berry", "grape", "kiwi", "melon",\n    // ... add all your 300 words here ...\n    "zebra"\n];\n\n// Function to get a random word from the array\nfunction getRandomWord() {\n    global $WORDS;\n    return $WORDS[array_rand($WORDS)];\n}\n\n// Example usage\n$captchaWord = getRandomWord();\necho $captchaWord; // This will output one of the words randomly each time\n?>