class Context {\n    private $strategy;\n\n    public function setStrategy(ConditionHandler $strategy) {\n        $this->strategy = $strategy;\n    }\n\n    public function execute() {\n        if ($this->strategy) {\n            $this->strategy->execute();\n        }\n    }\n}\n\n$context = new Context();\n\n$strategyType = 'condition_a'; // this comes from some input or context\n$strategies = [\n    'condition_a' => new HandlerA(),\n    'condition_b' => new HandlerB(),\n];\n\nif (isset($strategies[$strategyType])) {\n    $context->setStrategy($strategies[$strategyType]);\n}\n$context->execute();