<?php\n// Start by setting the cookie and handling logic first\nif (isset($_COOKIE["bg1"])) {\n    $img_no = $_COOKIE["bg1"] + 1;\n} else {\n    $img_no = 1;\n}\n\n$filename = 'bg_' . sprintf("%03d", $img_no) . '.jpg';\n\nif (!file_exists("img/" . $filename)) {\n    $img_no = 1;\n    $filename = 'bg_' . sprintf("%03d", $img_no) . '.jpg';\n}\n\nsetcookie("bg1", $img_no, time() + 86400);\n\n// Now output your HTML\nheader('Content-Type: image/jpeg');\nprint '<img src="img/' . htmlspecialchars($filename) . '" alt="" height="175" width="800"> ';\n?>