$query = $this->db->query('\n    SELECT \n        archives.id, \n        archives.signature, \n        type_of_source.description AS source_description, \n        media_type.description AS media_description, \n        origin.description AS origin_description \n    FROM archives\n    JOIN type_of_source ON archives.type_of_source_id = type_of_source.id\n    JOIN media_type ON type_of_source.media_type_id = media_type.id\n    JOIN origin ON archives.origin_id = origin.id\n    ORDER BY archives.id ASC\n');\n\nforeach ($query->result_array() as $row) {\n    echo 'ID: ' . $row['id'] . '\n';\n    echo 'Signature: ' . $row['signature'] . '\n';\n    echo 'Source Description: ' . $row['source_description'] . '\n';\n    echo 'Media Description: ' . $row['media_description'] . '\n';\n    echo 'Origin Description: ' . $row['origin_description'] . '\n';\n}