<?php\n\n$xmlString = <<<XML\n<root>\n    <set uniquename="value1">\n        <items>\n            <item>\n                <units>\n                    <!-- Other elements -->\n                </units>\n            </item>\n            <item>\n                <units>\n                    <!-- Other elements -->\n                    <url>http://www.something</url>\n                </units>\n            </item>\n        </items>\n    </set>\n    <set uniquename="value2">\n        <items>\n            <item>\n                <units>\n                    <!-- Other elements -->\n                </units>\n            </item>\n            <item>\n                <units>\n                    <!-- Other elements -->\n                    <url>http://www.something2</url>\n                </units>\n            </item>\n        </items>\n    </set>\n</root>\nXML;\n\n// Load the XML string into a SimpleXMLElement object\n$xml = simplexml_load_string($xmlString);\n\n// XPath query to find all 'url' elements within 'set' with 'uniquename="value2"'\n$urls = $xml->xpath('//set[@uniquename="value2"]/items/item/units/url');\n\n// Output the first URL found (assuming there is at least one)\nif (!empty($urls)) {\n    echo "URL: " . (string)$urls[0] . PHP_EOL;\n} else {\n    echo "No URL found for set with uniquename='value2'." . PHP_EOL;\n}\n?>