// Example of using generic IDs with gettext\n\n$domain = 'your_app';\n$language = 'en_US'; // Set language as needed\n\nputenv("LANG=$language");\nsetlocale(LC_ALL, $language);\nbindtextdomain($domain, "./LocaleData");\ntextdomain($domain);\n\n// Use a generic ID\necho gettext('hello.message');\n\n// In the English .po file:\n// msgid "hello.message"\n// msgstr "Hi there!"\n\n// If you change the English text, update it in the po file without changing IDs in your source code.