$s = curl_init();\n\n// URL to get the HTML of a specific Wikipedia article\n$url = 'https://en.wikipedia.org/w/api.php?format=json&action=parse&prop=text&page=' . urlencode($article_name);\n\ncurl_setopt($s, CURLOPT_URL, $url);\ncurl_setopt($s, CURLOPT_HEADER, false);\ncurl_setopt($s, CURLOPT_RETURNTRANSFER, 1);\n\n$response = curl_exec($s);\nif ($response === false) {\n    echo 'Curl error: ' . curl_error($s);\n} else {\n    $decodedResponse = json_decode($response, true);\n    if (isset($decodedResponse['parse']['text'])) {\n        // This contains the HTML of the article\n        $articleHTML = $decodedResponse['parse']['text']['*'];\n        echo $articleHTML;\n    } else {\n        echo 'Error parsing response: ' . print_r($decodedResponse, true);\n    }\n}\n\ncurl_close($s);