// Assuming $pdo is your PDO connection object and $list is your array of IDs\n$list = [1, 2, 3, 4]; // Example list of IDs\n\n// Create a string of placeholders for the IN clause\n$placeholders = implode(',', array_fill(0, count($list), '?'));\n\n// Prepare the SQL query\n$sql = "UPDATE foo SET flag=true WHERE id IN ($placeholders)";\n$stmt = $pdo->prepare($sql);\n\n// Execute the query with the list of IDs\n$stmt->execute($list);