if(isset($_POST["Return"])) {\n    Validate();\n}\n\nfunction Validate() {\n    if(!empty($_POST["From"]) && !empty($_POST["Body"])) {\n        Send_Email();\n    } else {\n        Fuss();\n    }\n}\n\nfunction Send_Email(&$alert) {\n    $alert = "Lorem Ipsum";\n    mail("", "", $_POST["Body"], "From: " . $_POST["From"]);\n}\n\nfunction Fuss(&$alert) {\n    $alert = "Dolor Sit";\n}\n\nfunction Alert($alert) {\n    if(!is_null($alert)) echo $alert;\n}\n\n$Alert = null; // Initialize the variable\nValidate();\nAlert($Alert); // Pass by reference to retain changes