<?php\n// Retrieve the original requested URL\n$requested_url = isset($_GET['url']) ? htmlspecialchars($_GET['url'], ENT_QUOTES, 'UTF-8') : 'Unknown';\n\n// Log or display the missed URL\necho "<h1>Page Not Found</h1>";\necho "<p>The page you were looking for: <strong>$requested_url</strong> was not found.</p>";\n\n// Optionally, log this information to a file (be cautious with logging sensitive data)\nfile_put_contents('404_log.txt', date('Y-m-d H:i:s') . " - Not Found: $requested_url\n", FILE_APPEND);\n?>