class Foo {\n    protected static $default = 'DEFAULT';\n\n    public static function doSomething($param = FALSE) {\n        $param = ($param === FALSE) ? static::$default : $param;\n        return $param;\n    }\n}\n\nclass Bar extends Foo {\n    protected static $default = 'NEW DEFAULT FOR CHILD CLASS';\n}\n\necho Foo::doSomething() . "\n"; \n// echoes 'DEFAULT'\n\necho Bar::doSomething() . "\n"; \n// echoes 'NEW DEFAULT FOR CHILD CLASS'