<?php\n// Ensure the stats functions are available\nif (!function_exists('stats_dens_normal')) {\n    die('The stats extension is not loaded or enabled.');\n}\n\n$mean = 10; // Peak at 10 o'clock\n$sigma = 3; // Standard deviation, adjust as needed\n\n// Generate points for each hour from 0 to 24\n$values = [];\nfor ($hour = 0; $hour < 24; $hour++) {\n    // Calculate the density of the normal distribution at this point\n    $density = stats_dens_normal($hour, $mean, $sigma);\n    \n    // Store the value\n    $values[$hour] = $density;\n}\n\n// Output the values\nforeach ($values as $hour => $value) {\n    echo "Hour $hour: $value\n";\n}\n?>