function fetchAssocWithTypes($result) {\n    $assoc = mysql_fetch_assoc($result);\n    if ($assoc) {\n        // Assuming you know the structure of your database, manually cast types\n        $assoc['id'] = (int)$assoc['id']; // Cast 'id' to integer\n        $assoc['price'] = (float)$assoc['price']; // Cast 'price' to float\n        $assoc['created_at'] = \DateTime::createFromFormat('Y-m-d H:i:s', $assoc['created_at']); // Convert date string to DateTime object\n\n        // Add more type conversions as needed based on your database schema\n    }\n    return $assoc;\n}\n\n// Usage example:\n$result = mysql_query("SELECT id, price, created_at FROM products WHERE id = 1");\n$product = fetchAssocWithTypes($result);