// Your multidimensional array\n$array = array(\n    "Part1" => array(\n        "Subpart1" => array(0, 1),\n        "Subpart2" => array(1, 0)\n    ),\n    "Part2" => array(0),\n    "Part3" => array(0, 1, 0)\n);\n\n// Serialize the array\n$serializedArray = serialize($array);\n\n// Database connection\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// SQL statement to insert serialized array\n$sql = "INSERT INTO your_table (id, array_column) VALUES (1, ?)";\n\n$stmt = $conn->prepare($sql);\n\n// Bind the parameters and execute\nif ($stmt === false) {\n    die("Error in prepare: " . $conn->error);\n}\n$stmt->bind_param("s", $serializedArray);\n$stmt->execute();\n\necho "New record created successfully";\n$conn->close();