<?php\nfunction combineAndCompressJs($jsFiles) {\n    $combined = '';\n    foreach ($jsFiles as $file) {\n        if (file_exists($file)) {\n            // Read the file content and append to combined string\n            $combined .= file_get_contents($file);\n        } else {\n            echo "File not found: $file<br>";\n        }\n    }\n\n    // Minify JavaScript using JSMin\n    require_once 'jsmin.php'; // Make sure you have jsmin.php or include a similar library\n    try {\n        return JSMin::minify($combined);\n    } catch (Exception $e) {\n        echo "Error minifying JavaScript: " . $e->getMessage();\n        return '';\n    }\n}\n\n$jsFiles = [\n    'includes/js/file1.js',\n    'includes/js/file2.js',\n    // Add the rest of your JS file paths here\n];\n\n// Combine and compress all JS files into one string\n$compressedJs = combineAndCompressJs($jsFiles);\n\n// Optionally, you can save it to a new .js file or output directly\nfile_put_contents('combined-min.js', $compressedJs);\n?>