<?php\nfunction getVisitorCountry() {\n    $ip = $_SERVER['REMOTE_ADDR'];\n    // Using ipinfo.io to get geolocation data\n    $response = @file_get_contents("http://ipinfo.io/{$ip}/json");\n    if ($response) {\n        $data = json_decode($response);\n        return $data->country;\n    }\n    return null;\n}\n\nfunction showContentBasedOnCountry() {\n    $visitorCountry = getVisitorCountry();\n    if ($visitorCountry == 'FI') { // FI is the ISO code for Finland\n        echo '<div>Special content for visitors from Finland!</div>';\n    } else {\n        echo '<div>Standard content.</div>';\n    }\n}\n\n// Call this function in your theme files where you want the content to appear\nshowContentBasedOnCountry();\n?>