<?php\nheader('Content-Type: text/xml');\n\n$xml = new SimpleXMLElement('<chart></chart>');\n$xml->addChild('chart_type', 'bar_2d');\n$xml->addChild('x_axis_name', 'Month');\n$xml->addChild('y_axis_name', 'Sales');\n$xml->addChild('data', '');\n\n$dateValues = [\n    ['Jan', 45],\n    ['Feb', 108],\n    ['Mar', 92],\n    ['Apr', 30],\n    ['May', 76]\n];\n\nforeach ($dateValues as $value) {\n    $dataset = $xml->data->addChild('set');\n    $dataset->addAttribute('label', $value[0]);\n    $dataset->addAttribute('value', $value[1]);\n}\n\necho $xml->asXML();\n?>