$xmlString = <<<XML\n<items>\n   <ITEM>My text!</ITEM>\n</items>\nXML;\n\n$doc = new DOMDocument;\n$doc->loadXML($xmlString);\n$xpath = new DOMXPath($doc);\n\n// Select all nodes within 'items'\n$nodes = $xpath->query("/items/*");\n\n$result = [];\nforeach ($nodes as $node) {\n    // Check node name case-insensitively\n    if (strcasecmp($node->nodeName, "item") === 0) {\n        $result[] = $node->textContent;\n    }\n}\n\nprint_r($result);