<?php\n// Read the file\n$hFile = "../uploads/james.json";\n$jsonData = file_get_contents($hFile);\n\n// Decode JSON data\n$data = json_decode($jsonData, true);\n\nfunction extractBookmarks(array $nodes) {\n    foreach ($nodes as $node) {\n        if (isset($node['uri'])) {\n            // Output the bookmark URL\n            echo htmlspecialchars($node['uri']) . "<br>";\n        }\n        if (isset($node['children'])) {\n            // Recursively extract bookmarks from children nodes\n            extractBookmarks($node['children']);\n        }\n    }\n}\n\n// Assuming bookmarks are under 'roots' -> 'bookmarksMenuFolder' or similar\nextractBookmarks($data['roots']['bookmarkBar']['children']);\nextractBookmarks($data['roots']['otherBookmarks']['children']);\nextractBookmarks($data['roots']['unfiledBookmarks']['children']);\n?>