$query = "SELECT * FROM tblProducts WHERE ProductId ='$SCId'; SELECT * FROM tblProducts WHERE Cat ='$CatType' AND Type ='$TypeType'";\n\nif ($result = mysqli_multi_query($connection, $query)) {\n    do {\n        if ($row = mysqli_fetch_assoc(mysqli_store_result($connection))) {\n            // Process the result set\n            print_r($row);\n        }\n    } while (mysqli_next_result($connection));\n} else {\n    die ("Query failed with error message: \"" . mysqli_error($connection) . '"');\n}\n\nmysqli_close($connection);